  #{quan,jdbcType=DECIMAL}, #{quanUom,jdbcType=VARCHAR}, #{commodity,jdbcType=VARCHAR}, 
  #{location,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{del,jdbcType=VARCHAR}
  )

mapper.update:
